import json

def raw2dict(raw):
    try:
        return json.loads(bytes.fromhex(raw).decode('utf-8')) # read raw hex to json as dict
    except json.JSONDecodeError as e:
        return ValueError(f"Invalid JSON data: {e}")

def decrypt(maps, data):
    maps = dict(zip(maps.values(), maps.keys())) # reverse key to value, value to key
    return ''.join([maps[value] if value in maps else value for value in data.split(' ')])

